var type = sumo2.dialog.GetParamFromDialog("d_article_cat","type");
var cat="";
if(type==="N") {
	cat= document.getElementById('category_new_article').value;
} else if(type==="T") {
	cat = document.getElementById('category_new_article_translate').value;
} else {
	cat= document[document.d_article_cat.edit_id.value].category_edit_article.value;
}
var dialog = document.getElementById('d_article_cat_id');
var inputs = dialog.getElementsByTagName('input');
if(cat.length>3) {
	var cats=cat.split("#??#");
	var lg=cats.length-1;
	for(var i=0,len=inputs.length;i<len;i++) {
		for(var j=0; j<lg; j++) {
			if(inputs[i].type == 'checkbox' && inputs[i].value==cats[j]) {
				inputs[i].checked = true;
			}
		}
	}
}


