$(".tab_content").hide(); 
var tabVal = sumo2.accordion.GetParamFromAccordion("a_settings","tab_val");
var endTab = "";
if(tabVal == false) {
	document.getElementById('settings_current_tab').value="#user1";
	endTab = "#user1";
} else {
	document.getElementById('settings_current_tab').value="#"+tabVal;
	endTab = "#"+tabVal;
}
$(endTab+"tab").addClass("active").show();
$(endTab).show();

$("ul.tabs li").click(function() {
	$("ul.tabs li").removeClass("active");
	$(this).addClass("active"); 
	$(".tab_content").hide();
	var activeTab = $(this).find("a").attr("href");
	var paramVal = activeTab.replace("#","");
	sumo2.accordion.SetParamForAccordion("a_settings","tab_val="+paramVal);
	document.getElementById('settings_current_tab').value=activeTab;
	$(activeTab).fadeIn(); 
	return false;
});
