$('.S_RND').contextMenu('myMenuS_RND', {
  bindings: {
	'rename': function(t) {
		var k =  t.id.split("#");	
		sumo2.dialog.NewDialog('d_menus_edit_i', 'id='+k[0]+'$!$mode=not');
	},
	'show': function(t) {
		sumo2.menu.showMenu(t.id);
	},
	'new': function(t) {
		sumo2.dialog.NewDialog('d_menus_new_i', 'id='+t.id);
	},
	'delete': function(t) {
		sumo2.dialog.NewConfirmation(sumo2.language.VARIABLES.WARNING,sumo2.language.VARIABLES.MENU_12,250,200,function() {
			  sumo2.menu.DeleteItem(t.id);
		});
	}
  }
});
